$.ajaxSetup({async:true});$.urlParam=function(name){var results=new RegExp("[\\?&]"+name+"=([^&#]*)").exec(decodeURIComponent(window.location.href));if(results==null){return null}else{return results[1]||0}};function getPageName(url){var index=url.lastIndexOf("/")+1;var filenameWithExtension=url.substr(index);var filename=filenameWithExtension.split(".")[0];return filename}function setActiveOption(e){var page;var currentURL=$(location).attr("href");if(typeof(e)==="undefined"){page=getPageName(currentURL);pageInfo=currentURL.split("/");folder=(pageInfo.pop()=="")?pageInfo[pageInfo.length-1]:pageInfo.pop();page=folder+"-"+page}else{page=e}$("#sidenav_"+page).addClass("active")}$(document).ready(function(){setActiveOption()});